SELECT USERNAME, SS.NAME AS SET_NAME, C2.NAME AS CATEGORY_NAME, QUESTION, ANSWER FROM APP_USER AU JOIN STUDY_SET SS ON AU.USER_ID = SS.OWNER_ID JOIN STUDY_SET_CARD SSC ON SS.STUDY_SET_ID = SSC.STUDY_SET_ID JOIN FLASHCARD F ON SSC.FLASHCARD_ID = F.FLASHCARD_ID JOIN CATEGORY C2 ON F.CATEGORY_ID = C2.CATEGORY_ID WHERE SS.STUDY_SET_ID = 4
